INSERT INTO `oc_emailtemplate` SET
`emailtemplate_key` = 'order.customer',
`emailtemplate_label` = 'Order Confirm - Customer',
`emailtemplate_type` = 'order',
`emailtemplate_preference` = 'essential',
`emailtemplate_mail_html` = 1,
`emailtemplate_mail_plain_text` = 0,
`emailtemplate_language_files` = 'mail/order_add,extension/module/emailtemplate/order',
`emailtemplate_order_product` = 1,
`emailtemplate_status` = 1,
`emailtemplate_default` = 1,
`emailtemplate_shortcodes` = 'none',
`emailtemplate_showcase` = 1,
`emailtemplate_modified` = NOW();

INSERT INTO `oc_emailtemplate_description` SET
`emailtemplate_id` = {_ID},
`language_id` = 0,
`emailtemplate_description_subject` = '{{ text_subject_customer }}',
`emailtemplate_description_preview` = '{{ text_preheader }}',
`emailtemplate_description_content1` = '&lt;table border=&quot;0&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; align=&quot;center&quot; class=&quot;table-heading&quot; style=&quot;width:auto;&quot;&gt;   &lt;tbody&gt;   &lt;tr&gt;     &lt;td width=&quot;2&quot;&gt;&lt;/td&gt;     &lt;td class=&quot;heading2&quot;&gt;&lt;strong&gt;{{ text_heading }}&lt;/strong&gt;     &lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;     &lt;td style=&quot;font-size:1px; line-height:3px; height:3px;&quot; height=&quot;3&quot; width=&quot;2&quot;&gt;&lt;/td&gt;     &lt;td style=&quot;font-size:1px; line-height:3px; height:3px;&quot; height=&quot;3&quot;&gt;&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;     &lt;td style=&quot;font-size:1px; line-height:1px; height:1px;&quot; bgcolor=&quot;#e8e8e8&quot; height=&quot;1&quot; width=&quot;2&quot;&gt;&lt;/td&gt;     &lt;td style=&quot;font-size:1px; line-height:1px; height:1px;&quot; bgcolor=&quot;#e8e8e8&quot; height=&quot;1&quot;&gt;&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;     &lt;td style=&quot;font-size:1px; line-height:1px; height:1px;&quot; height=&quot;15&quot; width=&quot;2&quot;&gt;&lt;/td&gt;     &lt;td style=&quot;font-size:1px; line-height:15px; height:15px;&quot; height=&quot;15&quot;&gt;&lt;/td&gt;   &lt;/tr&gt;   &lt;/tbody&gt; &lt;/table&gt; {{ text_greeting }}&lt;br&gt; &lt;div&gt;{% if instruction %}   &lt;br&gt;&lt;b&gt;{{ text_instruction }}&lt;/b&gt;&lt;br&gt;   {{ instruction }}   {% endif %}&lt;/div&gt; &lt;div&gt;{% if download_link and order_complete_status %}   &lt;br&gt;{{ text_download }}   &lt;table cellspacing=&quot;0&quot; cellpadding=&quot;0&quot;&gt;     &lt;tbody&gt;     &lt;tr&gt;       &lt;td&gt;         &lt;table class=&quot;link&quot; cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; width=&quot;100%&quot; align=&quot;left&quot; style=&quot;width:100%;&quot;&gt;           &lt;tbody&gt;           &lt;tr&gt;             &lt;td&gt;               &lt;a href=&quot;{{ download_link|replace({\'&amp;\':\'&\'}) }}&quot; target=&quot;_blank&quot;&gt;&lt;b&gt;{{ download_link_text }} &lt;/b&gt;&lt;/a&gt;             &lt;/td&gt;           &lt;/tr&gt;           &lt;/tbody&gt;         &lt;/table&gt;       &lt;/td&gt;     &lt;/tr&gt;     &lt;/tbody&gt;   &lt;/table&gt;&lt;/div&gt; {% endif %}',
`emailtemplate_description_content2` = '&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; class=&quot;emailtemplate-spacing&quot; style=&quot;width:100%&quot;&gt; 	&lt;tbody&gt; 		&lt;tr&gt; 			&lt;td height=&quot;15&quot; style=&quot;font-size:1px; line-height:15px; mso-margin-top-alt:1px&quot;&gt;&amp;nbsp;&lt;/td&gt; 		&lt;/tr&gt; 	&lt;/tbody&gt; &lt;/table&gt;  &lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; class=&quot;table-stack&quot; style=&quot;width:100%&quot;&gt; 	&lt;tbody&gt; 		&lt;tr&gt; 			&lt;td class=&quot;table-cell-stack&quot; style=&quot;vertical-align:top;width:33.3%;padding:0 10px 10px 0;&quot; width=&quot;33.3%&quot;&gt; 			&lt;table cellpadding=&quot;10&quot; cellspacing=&quot;0&quot; class=&quot;table-info table-stack&quot; style=&quot;width:100%&quot;&gt; 				&lt;thead&gt; 					&lt;tr&gt; 						&lt;th style=&quot;text-align:left;padding:10px 8px;&quot;&gt;&lt;strong&gt;{{ text_order_id }}&lt;/strong&gt;&lt;/th&gt; 					&lt;/tr&gt; 				&lt;/thead&gt; 				&lt;tbody&gt; 					&lt;tr&gt; 						&lt;td bgcolor=&quot;#fafafa&quot; style=&quot;background:#fafafa;padding:6px 10px;vertical-align:top&quot;&gt;{{ order_id }}{{ invoice_no }}&lt;/td&gt; 					&lt;/tr&gt; 				&lt;/tbody&gt; 			&lt;/table&gt; 			&lt;/td&gt; 			&lt;td class=&quot;table-cell-stack&quot; style=&quot;vertical-align:top;width:33.3%;padding:0 10px 10px 10px;&quot; width=&quot;33.3%&quot;&gt; 			&lt;table cellpadding=&quot;10&quot; cellspacing=&quot;0&quot; class=&quot;table-info table-stack&quot; style=&quot;width:100%&quot;&gt; 				&lt;thead&gt; 					&lt;tr&gt; 						&lt;th style=&quot;text-align:left;padding:10px 8px;&quot;&gt;&lt;strong&gt;{{ text_date_added }}&lt;/strong&gt;&lt;/th&gt; 					&lt;/tr&gt; 				&lt;/thead&gt; 				&lt;tbody&gt; 					&lt;tr&gt; 						&lt;td bgcolor=&quot;#fafafa&quot; style=&quot;background:#fafafa;padding:6px 10px;vertical-align:top&quot;&gt;{{ date_added }}&lt;/td&gt; 					&lt;/tr&gt; 				&lt;/tbody&gt; 			&lt;/table&gt; 			&lt;/td&gt; 			&lt;td class=&quot;table-cell-stack&quot; style=&quot;vertical-align:top;width:33.3%;padding:0 0 10px 10px;&quot; width=&quot;33.3%&quot;&gt; 			&lt;table cellpadding=&quot;10&quot; cellspacing=&quot;0&quot; class=&quot;table-info table-stack&quot; style=&quot;width:100%&quot;&gt; 				&lt;thead&gt; 					&lt;tr&gt; 						&lt;th style=&quot;text-align:left;padding:10px 8px;&quot;&gt;&lt;strong&gt;{{ text_order_status }}&lt;/strong&gt;&lt;/th&gt; 					&lt;/tr&gt; 				&lt;/thead&gt; 				&lt;tbody&gt; 					&lt;tr&gt; 						&lt;td bgcolor=&quot;#fafafa&quot; style=&quot;background:#fafafa;padding:6px 10px;vertical-align:top&quot;&gt;{{ order_status }}&lt;/td&gt; 					&lt;/tr&gt; 				&lt;/tbody&gt; 			&lt;/table&gt; 			&lt;/td&gt; 		&lt;/tr&gt; 	&lt;/tbody&gt; &lt;/table&gt;  &lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; class=&quot;emailtemplate-spacing&quot; style=&quot;width:100%&quot;&gt; 	&lt;tbody&gt; 		&lt;tr&gt; 			&lt;td height=&quot;10&quot; style=&quot;font-size:1px; line-height:10px; mso-margin-top-alt:1px&quot;&gt;&amp;nbsp;&lt;/td&gt; 		&lt;/tr&gt; 	&lt;/tbody&gt; &lt;/table&gt;  &lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; class=&quot;table-stack&quot; style=&quot;width:100%&quot;&gt; 	&lt;tbody&gt; 		&lt;tr&gt; 			&lt;td bgcolor=&quot;#fafafa&quot; class=&quot;table-cell-stack&quot; style=&quot;background:#fafafa;vertical-align:top;border:1px solid #e0e0e0;&quot;&gt; 			&lt;table cellpadding=&quot;10&quot; cellspacing=&quot;0&quot; class=&quot;table-info table-stack&quot; style=&quot;width:100%&quot;&gt; 				&lt;thead&gt; 					&lt;tr&gt; 						&lt;th style=&quot;text-align:center;border:none;border-bottom:1px solid #e0e0e0;&quot;&gt;{{ text_payment_address }}&lt;/th&gt; 					&lt;/tr&gt; 				&lt;/thead&gt; 				&lt;tbody&gt; 					&lt;tr&gt; 						&lt;td bgcolor=&quot;#fafafa&quot; style=&quot;background:#fafafa;padding:6px 10px;vertical-align:top;border:none;&quot;&gt; 						&lt;div style=&quot;line-height:18px;padding:0 0 5px 0;&quot;&gt;{{ payment_address }}&lt;/div&gt;  						&lt;div&gt;&lt;strong&gt;{{ text_payment_method }}&lt;/strong&gt; {{ payment_method }}&lt;/div&gt; 						&lt;/td&gt; 					&lt;/tr&gt; 				&lt;/tbody&gt; 			&lt;/table&gt; 			&lt;/td&gt; 			&lt;td bgcolor=&quot;#fafafa&quot; class=&quot;table-cell-stack&quot; style=&quot;background:#fafafa;vertical-align:top;border:1px solid #e0e0e0;{{ not shipping_address ? \'display:none;\' }}&quot;&gt; 			&lt;table cellpadding=&quot;10&quot; cellspacing=&quot;0&quot; class=&quot;table-info table-stack&quot; style=&quot;width:100%&quot;&gt; 				&lt;thead&gt; 					&lt;tr&gt; 						&lt;th style=&quot;text-align:center;border:none;border-bottom:1px solid #e0e0e0;&quot;&gt;{{ text_shipping_address }}&lt;/th&gt; 					&lt;/tr&gt; 				&lt;/thead&gt; 				&lt;tbody&gt; 					&lt;tr&gt; 						&lt;td bgcolor=&quot;#fafafa&quot; style=&quot;background:#fafafa;padding:6px 10px;vertical-align:top;border:none;&quot;&gt; 						&lt;div style=&quot;line-height:18px;padding:0 0 5px 0;&quot;&gt;{{ shipping_address }}&lt;/div&gt;  						&lt;div&gt;&lt;strong&gt;{{ text_shipping_method }}&lt;/strong&gt; {{ shipping_method }}&lt;/div&gt; 						&lt;/td&gt; 					&lt;/tr&gt; 				&lt;/tbody&gt; 			&lt;/table&gt; 			&lt;/td&gt; 		&lt;/tr&gt; 	&lt;/tbody&gt; &lt;/table&gt; ',
`emailtemplate_description_content3` = '&lt;div&gt;{{ html_order_product }}&lt;/div&gt; &lt;div&gt;{% if order_comment %}&lt;br&gt;&lt;b&gt;{{ text_comment }}&lt;/b&gt; &lt;br&gt;{{ order_comment }} {% endif %}&lt;/div&gt; {% if customer_id %}&lt;div&gt;&lt;br&gt;&lt;b&gt;{{ text_order_link }} &lt;/b&gt;&lt;/div&gt; &lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;   &lt;tbody&gt;   &lt;tr&gt;     &lt;td&gt;       &lt;div class=&quot;table-responsive&quot;&gt;         &lt;table align=&quot;left&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; class=&quot;link&quot; style=&quot;width:100%;&quot; width=&quot;100%&quot;&gt;           &lt;tbody&gt;           &lt;tr&gt;             &lt;td&gt;&lt;a href=&quot;{{ order_link|replace({\'&amp;\':\'&\'}) }}&quot; target=&quot;_blank&quot;&gt;&lt;b&gt;{{ order_link_text }} &lt;/b&gt;&lt;/a&gt;&lt;/td&gt;           &lt;/tr&gt;           &lt;/tbody&gt;         &lt;/table&gt;       &lt;/div&gt;     &lt;/td&gt;   &lt;/tr&gt;   &lt;/tbody&gt; &lt;/table&gt; {% endif %}&lt;div class=&quot;last&quot;&gt;&lt;br&gt;{{ text_footer }}   &lt;br&gt;   &lt;b&gt;{{ store_name }}&lt;/b&gt; &lt;/div&gt;';
