<?php class ModelExtensionModuleCartId extends Model{public function get3000($product_id,$quantity=1,$option=array(),$recurring_id=0,$price=0){$query=$this->db->query("SELECT cart_id FROM ".DB_PREFIX."cart WHERE api_id = '".(isset($this->session->data['api_id'])?(int)$this->session->data['api_id']:0)."' AND customer_id = '".(int)$this->customer->getId()."' AND session_id = '".$this->db->escape($this->session->getId())."' AND product_id = '".(int)$product_id."' AND recurring_id = '".(int)$recurring_id."' AND `option` = '".$this->db->escape(json_encode($option))."'");if($query->num_rows){return $query->row['cart_id'];}else{return 0;}}public function getCartId($product_id,$quantity=1,$option=array(),$recurring_id=0,$price=0){$version=str_replace(".","",VERSION);if($version>3000){return $this->get3000($product_id,$quantity,$option,$recurring_id,$price);}}}